mapper.select:
select c.menu_id, c.menu_name, c.url, c.sequence, c.icon, c.parent_id, c.create_user, c.create_time, c.update_user, c.update_time, c.status, c.desensitize_status, c.module_id, c.remark, f.menu_name as parent_name from sys_menu c left join sys_menu f on c.parent_id = f.menu_id
and c.module_id= #{moduleId}
and c.status = #{status,jdbcType=SMALLINT}
and c.parent_id = #{parentId}
order by c.sequence,c.update_time

select t1.menu_id , t1.parent_id , t1.menu_name , t1.status, t1.url, t1.icon, t1.desensitize_status , t2.status as parent_status, t2.url as parent_url from sys_menu t1 left join sys_menu t2 on t1.parent_id = t2.menu_id where t1.module_id = #{moduleId}
and parent_id = #{parentId}
order by t1.sequence

select
count(menu_id)
from sys_menu
where module_id =#{moduleId} and url = #{url,jdbcType=VARCHAR}

    and menu_id != #{menuId,jdbcType=VARCHAR}


order by sequence
