mapper.select:
SELECT DISTINCT rm.menu_id AS entMenuId, m.sys_menu_code as id, m.menu_name AS menuName, m.url AS url, m.sequence AS sequence, m.icon AS icon, m.menu_type AS menuType, m.parent_id AS parentId, m.parent_name AS parentName, m.create_user AS createUser, m.create_time AS createTime, m.update_user AS updateUser, m.update_time AS updateTime, m.remark, m.status, m.auth_status as authStatus, m.desensitize_status as desensitizeStatus
,udp.is_desensite AS desensite
FROM ent_role_menu rm LEFT JOIN ent_menu m ON rm.menu_id = m.id LEFT JOIN ent_user_data_permission udp ON m.id = udp.menu_id WHERE rm.role_id = #{roleId}
And udp.user_id = #{userId}
AND m.sys_code = #{sysCode} AND (m.sys_menu_code = #{menuId} OR m.parent_id = #{menuId}) AND m.menu_type != 2 AND m.url != '' AND m.url is not null AND m.status = 1 ORDER BY m.create_time ASC
